import xmltodict, json
from flask import Flask, request
import xml.etree.ElementTree as ET
from pm4py.objects.dcr.hierarchical.obj import HierarchicalDcrGraph
from simulatorInit import createDCRgraph, ExecuteEventOnGraph
app = Flask(__name__)
graphIDs = []
simIDs = []
graphIDs.append(test)

def findGraph(GID):
    for obj in graphIDs:
        if obj['GID'] == GID:
            return obj
    return None

def findSim(SID):
    for obj in simIDs:
        if obj['SID'] == SID:
            return obj
    return None
            
def replaceAllInstances(input, old, new):
    if isinstance(input, dict):
        return {k: replaceAllInstances(v, old, new) for k, v in input.items()}
    elif isinstance(input, list):
        return [replaceAllInstances(elem, old, new) for elem in input]
    elif input == old:
        return new
    return input
        

@app.post('/api/graphs/loadXML')
def loadXML():
    # TODO: Error handling
    xml = request.data
    dict = xmltodict.parse(xml)
    dict = replaceAllInstances(dict, 'null', None) # For easier python handling
    jsn = {'GID': graphIDs.__len__(), 'graph': dict}
    graphIDs.append(jsn)
    print(jsn['graph'])
    return jsn

@app.get('/api/graphs/<int:GID>')
def getGraph(GID):
    graph = findGraph(GID)
    if graph is not None:
        return graph
    return "Unknown GID"

# Creates a DCRGraph object of GID
@app.post('/api/graphs/<int:GID>/createSimulator')
def SimulateInit(GID):
    for obj in graphIDs:
        if obj['GID'] == GID:
            dcr = createDCRgraph(obj)
            jsn = {'SID': simIDs.__len__(), 'graph': dcr}
            simIDs.append(jsn)
            return {'SID': jsn['SID'], 'graph': dcr.__repr__()} #TODO: Return executable events
    return "Unknown GID"

@app.get('/api/simulator/<int:SID>')
def getSimulation(SID):
    sim = findSim(SID)
    if sim is not None:
        return {'SID': sim['SID'], 'graph': sim['graph'].__repr__()}
    return "Unknown SID"

@app.post('/api/simulator/<int:SID>/executeEvent/<string:event>')
def executeEvent(SID, event):
    sim = findSim(SID)
    if sim is not None:
        dcr = sim['graph']
        res = ExecuteEventOnGraph(dcr, event)
        if not isinstance(res, str):
            res = {'SID': SID, 'graph': res.__repr__()}
        return res
    return 'Unknown SID'